options.css:
* {
text-rendering: geometricPrecision !important;
}

#main a {
    font-family: "helvetica";
    font-weight: 300;
    font-size: 13.8px;
    color: black;
    border: 1px solid #ffffff00;
    border-radius: 4px;
    padding: 0 10px 1px 10px;
    margin-bottom: 4px;
    width: 100%;
}

#main a:hover{
    background-color: #ffffff00 !important;
    box-shadow: 0 8px 12px -6px #0037ff9e;
/*     border-bottom: 1px solid #0037ff00; */
    border-bottom: 1px solid #fff !important;
    border-top-color: #fff !important;
    border-left-color: #fff !important;
    border-right-color: #fff !important;
}


.column {
    padding-right: 20px;
}

.column > ul > li > a {
    font-weight: 500 !important;
    font-size: 12.3px !important;
}


.column div a.folder .icon {
    display: none !important;
}

.column div a.folder{
    font-weight: 500 !important;
    font-size: 12.3px !important;
}

.column div a.folder {
   margin-left: -16px
}

.column div a.folder:before {
    content: "";
    height: 4px;
    width: 4px;
    margin-bottom: 2px;
    border-radius: 100%;
    background-color: #0069ff;  
    display: inline-block;
    margin-right: 10px;
    border-color: #0069ff;
    border-width: 1px;
    border-style: solid;
}

.column div a.folder.open:before {
    border-color: #0069ff;
    background-color: #ffffff; 

}


.column div a:hover.folder:before{
    border-color: #ff0000;
    background-color: #ff0000; 
}

.column:first-child > ul > li > div {
    padding-left: 24px !important;
}

.column:not(:first-child) > ul > li > div {
/*     padding-left: 0px !important; */
}

#main > .column:not(:first-child) > ul > li > a {
    position: relative;
}

#main > .column:not(:first-child) > ul > li > a:hover,
.folder:hover {

    box-shadow: none !important;
    border-color: #fff0 !important;
}

#main > .column:not(:first-child) > ul > li > a:after {

    content: "";
    height: 1px;
    width: calc( 100% - 20px );
    background-color: #0037ff61;
    position: absolute;
    left: 0px;
    bottom: 0;
}


.column:not(:first-child) > ul > li > a > .icon {
    display: none !important;
}

.column > ul > li > div {
    padding-left: 0 !important;
}



ul > li > div {
    position: relative !important;
}

ul > li > div div {
    padding-left: 0 !important;
}



ul > li > div div:before {
    content: "";
    display: block;
    height: calc( 100% - 8px );
    top: -17px;
    left: -3px;
    width: 2px;
    background-color: #0069ff21;
    position: absolute;
}

ul > li > div div ul li:last-child:before {
    content: "";
    display:block;
    position:absolute;
    height: 4px;
    width: 4px;
    border-radius: 100%;
    left: -5px;
    bottom: 18px;
    border-width: 1px;
    border-style: solid;
    border-color: #0069ff;
    background-color: #ffffff; 

}
